// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login'; // Ajusta la ruta según tu estructura
import Dashboard from './components/Dashboard'; // Ajusta la ruta según tu estructura
import PrivateRoute from './components/ProtectedRoute'; // Importa el componente de ruta privada

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Estado para la autenticación

    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
                <PrivateRoute path="/dashboard" element={<Dashboard />} isAuthenticated={isAuthenticated} />
                {/* Redirigir a login si la ruta no existe */}
                <Route path="*" element={<Navigate to="/login" />} /> 
            </Routes>
        </Router>
    );
}

export default App;
