import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearRoles = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [rolName, setRolName] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [isEditing, setIsEditing] = useState(false); // Estado para saber si estamos editando o creando
  const navigate = useNavigate();
  const { id } = useParams(); // Para obtener el ID de la URL (solo si estamos editando)

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else if (id) {
      // Si estamos editando un rol, cargamos los datos del rol
      setIsEditing(true);
      fetchRoleData(id); // Traer los datos del rol para editar
    }
  }, [navigate, id]);

  // Función para cargar los datos del rol (solo para editar)
  const fetchRoleData = async (roleId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({ severity: 'error', message: 'No se encontró el token de autenticación.' });
        return;
      }
      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get(`http://localhost:5000/api/roles/${roleId}`, config);
      if (response.data) {
        setRolName(response.data.rolName);
        setDescripcion(response.data.descripcion);
      }
    } catch (error) {
      console.error('Error al obtener los datos del rol:', error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al cargar los datos del rol.' });
    }
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!rolName || !descripcion) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const newRole = { rolName, descripcion };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      let response;
      if (isEditing) {
        // Si estamos editando el rol
        response = await axios.put(`http://localhost:5000/api/roles/${id}`, newRole, config);
      } else {
        // Si estamos creando un rol
        response = await axios.post('http://localhost:5000/api/roles/register', newRole, config);
      }

      // Verificar la respuesta de la API
      if (response.data.msg === 'El rol ya existe') {
        setShowAlert({
          severity: 'error',
          message: response.data.msg, // Mostrar el mensaje de error
        });
      } else if (response.data.msg === (isEditing ? 'Rol actualizado correctamente' : 'Rol registrado correctamente')) {
        setShowAlert({
          severity: 'success',
          message: response.data.msg, // Mostrar el mensaje de éxito
        });

        // Limpiar los campos del formulario si el rol se creó con éxito
        setRolName('');
        setDescripcion('');

        // Redirigir a la lista de roles después de 3 segundos
        setTimeout(() => {
          navigate('/seguridad/roles');
        }, 2000);
      }
    } catch (error) {
      console.error('Error al guardar el rol:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al guardar el rol. Inténtalo de nuevo.',
        });
      }
    }
  };

  // Función para eliminar el rol
  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({ severity: 'error', message: 'No se encontró el token de autenticación.' });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.delete(`http://localhost:5000/api/roles/deleteRol/${id}`, config);

      if (response.data.msg === 'Rol eliminado correctamente') {
        setShowAlert({ severity: 'success', message: response.data.msg });
        setTimeout(() => {
          navigate('/seguridad/roles');
        }, 2000);
      }
    } catch (error) {
      console.error('Error al eliminar el rol:', error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al eliminar el rol.' });
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000} // La alerta se cierra después de 2 segundos
          sx={{ mb: 4 }} // Agregar margen inferior a la alerta
        />
      )}

      <Typography variant="h4" gutterBottom>
        {isEditing ? 'Editar Rol' : 'Crear Nuevo Rol'}
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campo para Nombre del Rol */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Rol"
              value={rolName}
              onChange={(e) => setRolName(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Descripción */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
            />
          </Grid>

          {/* Botón para enviar el formulario */}
          <Grid item xs={12} sm={6}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              {isEditing ? 'Actualizar Rol' : 'Crear Rol'}
            </Button>
          </Grid>

          {/* Botón para eliminar el rol (solo cuando estamos editando) */}
          {isEditing && (
            <Grid item xs={12} sm={6}>
              <Button
                variant="contained"
                color="secondary"
                fullWidth
                onClick={handleDelete}
              >
                Eliminar Rol
              </Button>
            </Grid>
          )}
        </Grid>
      </form>
    </Box>
  );
};

export default CrearRoles;
