import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

const demoTheme = createTheme({
  palette: {
    mode: 'light',
  },
});

// Mapa de íconos según el DisplayName o MenuID
const iconMap = {
  Seguridad: <SecurityIcon />,
  Roles: <DescriptionIcon />,
  Usuarios: <PersonIcon />,
  Permisos: <LockIcon />,

  // Agrega más asociaciones aquí según sea necesario
};

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 40,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Bienvenido al Gestor de Control de Calidad!</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [menuItems, setMenuItems] = React.useState([]);
  const [open, setOpen] = React.useState(false); // Estado para manejar el menú desplegable

  const fetchMenus = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token);
    const username = decoded.user.username;

    try {
      const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
      setMenuItems(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  React.useEffect(() => {
    fetchMenus();
  }, []);

  // Crear la estructura del menú
  const navigation = menuItems.map(item => {
    if (item.ParentMenuID === 0) {
      // Elemento principal
      return {
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: iconMap[item.DisplayName] || <DescriptionIcon />, // Asignar ícono
        children: menuItems.filter(subItem => subItem.ParentMenuID === item.MenuID), // Submenús
      };
    }
    return null;
  }).filter(Boolean);

  return (
    <AppProvider
      navigation={navigation.map(item => ({
        segment: item.segment,
        title: item.title,
        icon: item.icon,
        children: item.children.length > 0 ? item.children.map(subItem => ({
          segment: subItem.DisplayName,
          title: subItem.DisplayName,
          icon: iconMap[subItem.DisplayName] || <DescriptionIcon />, // Asignar ícono al submenú
          onClick: () => navigate(subItem.MenuURL), // Navegar al submenú
        })) : [],
      }))}
      router={{
        pathname,
        navigate: (path) => navigate(path),
      }}
      theme={demoTheme}
    >
      <DashboardLayout>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
