import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios'; // Importar axios
import { jwtDecode } from 'jwt-decode'; // Importar jwtDecode correctamente

const demoTheme = createTheme({
  palette: {
    mode: 'light', // Establece el modo en 'light' para que sea claro
  },
});

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 4,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Dashboard content for {pathname}</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [menuItems, setMenuItems] = React.useState([]);

  // Función para obtener el menú desde la API
  const fetchMenus = async () => {
    const token = localStorage.getItem('token'); // Asegúrate de que el token esté almacenado aquí
    if (!token) return;

    const decoded = jwtDecode(token); // Decodifica el token para obtener el username
    const username = decoded.user.username; // Asegúrate de que el campo sea correcto en tu token

    try {
      const response = await axios.get(`/api/menus/${username}`); // Asegúrate de que esta sea la ruta correcta
      setMenuItems(response.data); // Actualiza el estado con los menús obtenidos
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  // Usa useEffect para llamar a fetchMenus al montar el componente
  React.useEffect(() => {
    fetchMenus();
  }, []);

  return (
    <AppProvider
      navigation={menuItems.map(item => ({
        segment: item.segment, // Cambia esto según el nombre de tu propiedad en el menú
        title: item.title, // Cambia esto según el nombre de tu propiedad en el menú
        icon: <DescriptionIcon />, // Puedes cambiar el icono según tu lógica
      }))}
      router={{
        pathname,
        push: (path) => navigate(path),
      }}
      theme={demoTheme} // Pasa el tema claro aquí
    >
      <DashboardLayout>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
