// src/components/ProtectedRoute.js
import React from 'react';
import { Navigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

const ProtectedRoute = ({ children }) => {
    const token = localStorage.getItem('token');
    console.log("Token in ProtectedRoute:", token); // Verificar que el token se obtiene

    if (!token) {
        console.log("Token not found. Redirecting to login.");
        return <Navigate to="/login" />;
    }

    try {
        const decoded = jwtDecode(token);
        console.log("Decoded token:", decoded); // Verificar contenido del token
        return children; // Renderiza el componente si el token es v치lido
    } catch (error) {
        console.error('Token inv치lido o expirado:', error);
        return <Navigate to="/login" />;
    }
};

export default ProtectedRoute;
